import React, { Component } from 'react';
import { createStore } from 'redux';
import { Provider, connect } from 'react-redux';
import { BrowserRouter as Router, Route, Link } from "react-router-dom";

class Header extends Component{
    constructor(props){
        super(props); 
        this.state = {
          name: this.props.tiger
        }
        if(this.state.name == ''){
            this.state = {
              lishow: 'none',
              blishow:'block'
            }
        }else{
          this.state = {
            lishow: 'block',
            blishow:'none'
          }
        }
        console.log(this.props)
    }
    componentWillReceiveProps(nextProps){
      let _this =this;
      if (_this.props.tiger !== nextProps.tiger){
          _this.setState({
            name:nextProps.tiger
          })
          if(nextProps.tiger== ''){
            _this.setState({
              lishow: 'none',
              blishow:'block'
            })
          }else{
            let data =  JSON.parse(sessionStorage.getItem("data"));
            _this.setState({
              lishow: 'block',
              blishow:'none',
              name:data.userInfo.uid
            })

            console.log(data.userInfo.uid)
            _this.setState({
              name:data.userInfo.uid
            })
          }
      }
    }
    render() {
      const { PayIncrease, PayDecrease } = this.props;
      return <div className="header">
        <Link to="/">
          <div className="logo"></div>
        </Link>
        <ul>
          <li><Link to="/about">About</Link></li>
          <li style={{display:this.state.blishow}}><Link to="/login">login</Link></li>
          <li style={{display:this.state.blishow}}><Link to="/register">register</Link></li>
          <li style={{display:this.state.lishow}}>欢迎：{this.state.name}</li>
        </ul>
    </div>
    }
}
//需要渲染什么数据
function mapStateToProps(state) {
  return {
    tiger: state
  }
}
//需要触发什么行为
function mapDispatchToProps(dispatch) {
  return {
    PayIncrease: () => dispatch({ type: false }),
    PayDecrease: () => dispatch({ type: true })
  }
}
Header = connect(mapStateToProps, mapDispatchToProps)(Header)
export default Header;
