import React, { Component } from 'react';

class Popup extends Component{
    constructor(){
        super(); 
        this.state = {
            display:this.props.display
        }
    }
    componentDidMount(){
        setTimeout(function(){
            this.setState({
                display:"none"
            })
        },800)
    }
    render() {
      return <div className="popup" style={{display:this.state.display}}>
           <p>{this.props.msg}</p>
        </div>
    }
}

export default Popup;
