import React, { Component } from 'react';
import Footer from '../component/footer';
import Header from '../component/header';

class About extends Component{
    constructor(){
      super(); 
      this.state={
        text:""
      }
    }
    componentDidMount() {
      var _this = this;
        fetch("http://localhost:3000/about").then(response => {
            return response.json()
          }).then( data => {
                _this.setState({
                  text:data.text[0].text
                })
                console.log(this.state.text)
          })
    }
    render() {
      return  <div>
      <Header></Header>
        <div class='about'>
            <h3>(纳斯达克股票代码：HX)</h3>
            <p>
            和信贷(纳斯达克股票代码：HX)(“和信贷”或“本公司”)，
            一家快速成长的中国网络借贷信息中介平台，今日宣布向云锋金融集团
            (“云锋金融”)旗下子公司管理的豁免公司Majik Fund SPC发行优先无抵押票据。
            云锋金融(香港证券交易所股票代码：376)，是一家创新型金融科技公司，为个人和机构投资者以及企业
            客户提供综合性投融资服务。
            和信贷向Majik Fund SPC 公司发行了共计2,000万美元的于2022年到期的三年期优先无抵押票据。
            该票据按照固定年利率12.0%计息，利息每半年支付一次。
            </p>
            {this.state.text}
        </div>
      <Footer></Footer>
      </div>
    }
}

export default About;
