import React, { Component } from 'react';

class Newslist extends Component{
    constructor() {
        super(); // 调用基类的所有初始化方法
        this.state = {
          list: []
        }
    }
    componentDidMount() {
        var _this = this;
        fetch("http://localhost:3000/newslist").then(response => {
            return response.json()
          }).then( data => {
              if(data.states == 200){
                _this.setState ({
                    list: data.list
                });
              }else{
                console.log("请求出错")
              }
          })
    }
    render() {
        let items = [];
        let _this = this;
        if(Object.keys(_this.state.list).length>0){
          var title = _this.state.list.title
          var img = _this.state.list.img

          console.log(img);

          for(var i=0; i < 10; i++){
            console.log(i);
            items.push( <li key={i}>
              <div className="news_title">
               <img src={img[i].img}/>
              <a href={"http://www.guandian.cn/"+title[i].href} target="_blank">
                <h3>{title[i].title}</h3>
              </a>
              </div>
            </li>);
          }
          return <ul>
              {items}
          </ul>
        }else{
          return <ul>
              <li>暂无数据</li>
          </ul>
        }
    }
}

export default Newslist;
