import React, { Component } from 'react';

class Newslist extends Component{
    constructor() {
        super(); // 调用基类的所有初始化方法
        this.state = {
          list: []
        }
    }
    componentDidMount() {
        var _this = this;
        fetch("http://localhost:3000/newslist").then(response => {
            return response.json()
          }).then( data => {
              if(data.states == 200){
                _this.setState ({
                    list: data.list
                });
              }else{
                console.log("请求出错")
              }
          })
    }
    render() {
        let items = [];
        let title = this.state.list.title
        console.log(title.lenght)
        if(title.lenght>0){
          for(let i; i<title.lenght; i++){
            items.push( <li>
              <div className="news_title">
              <a href={title[i].href}>
                <h3>{title[i].title}</h3>
              </a>
              </div>
            </li>);
          }
          return <ul>
              {items}
          </ul>
        }

    }
}

export default Newslist;
