import React, { Component } from 'react';

class Problem extends Component{
    constructor(){
        super(); 
        this.handleChange = this.handleChange.bind(this);
        this.qutSubmit = this.qutSubmit.bind(this);
        this.state = {
            text: ""
        }
    }
    qutSubmit(e){
        if(this.state.text == ""){
            alert("不能为空")
           return false     
        }
        let formData = {
            text:this.state.text
        }
        fetch("http://localhost:3000/problem",{
            method: 'post',
            body: formData,
        }).then(response => {
            return response.json()
            }).then( data => {
                if(data.states == 200){
                    console.log("444")
                }else{
                    console.log(data)
                }
            })
    }
    handleChange(e){
        let _this = this;
        _this.setState ({
            text: e.target.value
        });
    }
    render() {
      return  <form action="javascript:;" method="get">
      <textarea placeholder="请填入问题详情，我们会第一时间联系您！" onChange={this.handleChange} value={this.state.text} className="input_text" type="text" name="qutext">
      </textarea>
      <input onClick={this.qutSubmit} type="submit" className="subbtn" value="提交"/>
    </form>
    }
}

export default Problem;
