import React, { Component } from 'react';

class Newslist extends Component{
    constructor() {
        super(); // 调用基类的所有初始化方法
        this.state = {
          list: []
        }
    }
    componentWillMount() {
        var _this = this;
        fetch("http://localhost:3000/newslist").then(response => {
            return response.json()
          }).then( data => {
              if(data.states == 200){
                _this.state.list = data.list;
              }else{
                console.log("请求出错")
              }
          })
    }
    render() {
        let items = [];
        this.state.list.map((item, index) => {
            items.push( <li>
                <img src={item.src}/>
                <div className="news_title">
                  <h3>{item.title}</h3>
                  <p>{item.author}</p>
                </div>
            </li>);
        })
      return <ul>
        {items}
    </ul>
    }
}

export default Newslist;
